import axios from 'axios';
import React from "react";
import { Link } from "react-router-dom";
import { Button, Container, Divider, Icon, Table } from 'semantic-ui-react';
import { ENDERECO_API } from '../../views/util/constante';

class ListComprador extends React.Component{

    state = {
        openModal: false,
        idRemover: null,
        listaCompradores: []
      
    }

confirmaRemover = (id) => {

    this.setState({
    openModal: true,
    idRemover: id
     })  
}
setOpenModal = (val) => {

    this.setState({
        openModal: val
    })

};
remover = async () => {

    await axios.delete(ENDERECO_API + 'api/comprador/' + this.state.idRemover)
    .then((response) => {

        this.setState({ openModal: false })
        console.log('Comprador removido com sucesso.')

        axios.get(ENDERECO_API + "api/comprador")
        .then((response) => {
       
            this.setState({
                listaComprador: response.data
            })
        })
    })
    .catch((error) => {
        this.setState({  openModal: false })
        console.log('Erro ao remover um comprador.')
    })
};


    componentDidMount = () => {
      
        this.carregarLista();
      
    }

    carregarLista = () => {

        axios.get(ENDERECO_API + "api/comprador")
        .then((response) => {
          
            this.setState({
                listaCompradores: response.data
            })
        })

    };

    formatarData = (dataParam) => {

        if (dataParam == null || dataParam == '') {
            return ''
        }
        
        let dia = dataParam.substr(8,2);
        let mes = dataParam.substr(5,2);
        let ano = dataParam.substr(0,4);
        let dataFormatada = dia + '/' + mes + '/' + ano;

        return dataFormatada
    };

    render(){
        return(
            <div>

                <div style={{marginTop: '3%'}}>

                    <Container textAlign='justified' >

                        <h2> Comprador </h2>

                        <Divider />

                        <div style={{marginTop: '4%'}}>

                        <Link to={'/form-comprador'}>
                            <Button
                                inverted
                                circular
                                icon
                                labelPosition='left'
                                color='orange'
                                floated='right'
                            >
                                <Icon name='clipboard outline' />
                               Novo
                            </Button></Link>

                            <br/><br/><br/>
                      
                            <Table color='orange' sortable celled>

                                <Table.Header>
                                    <Table.Row>
                                        <Table.HeaderCell>Nome</Table.HeaderCell>
                                        <Table.HeaderCell>Valor de Comissão</Table.HeaderCell>
                                        <Table.HeaderCell>Contratado Em</Table.HeaderCell>
                                        <Table.HeaderCell>QTD Compras no Mês</Table.HeaderCell>
                                        <Table.HeaderCell>Endereço Residencial</Table.HeaderCell>
                                        <Table.HeaderCell>Trabalha em Home Office?</Table.HeaderCell>
                                        <Table.HeaderCell textAlign='center' width={2}>Ações</Table.HeaderCell>
                                    </Table.Row>
                                </Table.Header>
                          
                                <Table.Body>

                                    { this.state.listaCompradores.map(c => (

                                        <Table.Row>
                                            <Table.Cell>{c.nome}</Table.Cell>
                                            <Table.Cell>{c.comissao}</Table.Cell>
                                            <Table.Cell>{this.formatarData(c.contratadoEm)}</Table.Cell>
                                            <Table.Cell>{c.qtdComprasMediasMes}</Table.Cell>
                                            <Table.Cell>{c.enderecoResidencial}</Table.Cell>
                                            <Table.Cell>
                                                {c.trabahoHomeOffice === true &&
                                                    <span> Sim </span>
                                                }
                                                {c.trabahoHomeOffice === false &&
                                                    <span> Não </span>
                                                }
                                            </Table.Cell>
                                            <Table.Cell textAlign='center'>

                                            <Button
                                            inverted
                                            circular
                                            color='green'
                                            title='Clique aqui para editar os dados deste comprador'
                                            icon>
                                         <Link to="/form-comprador" state={{id: comprador.id}} style={{color: 'green'}}> <Icon name='edit' /> </Link>
                                         </Button> &nbsp;
                                                   
                                         <Button
                                         inverted
                                         circular
                                         color='red'
                                         title='Clique aqui para remover este comprador'
                                         icon
                                        onClick={e => this.confirmaRemover(comprador.id)}>
                                        <Icon name='trash' />
                                        </Button>

                                            </Table.Cell>
                                        </Table.Row>
                                    ))}

                               </Table.Body>
                           </Table>
                       </div>
                   </Container>
               </div>
               <Modal
                   			basic
                   			onClose={() => this.setOpenModal(false)}
                   			onOpen={() => this.setOpenModal(true)}
                   			open={this.state.openModal}
               			>
                   			<Header icon>
                       				<Icon name='trash' />
                       				<div style={{marginTop: '5%'}}> Tem certeza que deseja remover esse registro? </div>
                   			</Header>
                   			<Modal.Actions>
                       				<Button basic color='red' inverted onClick={() => this.setOpenModal(false)}>
                       					<Icon name='remove' /> Não
                       				</Button>
                       				<Button color='green' inverted onClick={() => this.remover()}>
                       					<Icon name='checkmark' /> Sim
                       				</Button>
                   			</Modal.Actions>
               			</Modal>
           </div>
           

       )
   }
}

export default ListComprador;
